    [Parameter] public string id { get; set; }
    private Product product;
    private Review newReview = new Review();

    protected override async Task OnInitializedAsync()
    {
        // Usa la instancia de ProductService para obtener el producto
        product = await ProductService.GetProductById(id);
    }

    private void GoBack()
    {
        // Usa la instancia de NavigationManager para navegar
        NavigationManager.NavigateTo("/catalog");
    }

    private async Task AddReview()
    {
        if (!string.IsNullOrWhiteSpace(newReview.UserName) && !string.IsNullOrWhiteSpace(newReview.Comment) && newReview.Stars > 0 && newReview.Stars <= 5)
        {
            // Agrega la nueva reseña al producto
            product.Reviews.Add(newReview);

            // Actualiza el producto en la base de datos
            await ProductService.UpdateProduct(product);

            // Limpia el formulario
            newReview = new Review();
        }
    }

